
/**
 * @class Ext.ux.ServerUtilities
 *
 * Utility Singleton
 * 
 * @private
 * @singleton
 * 
 */
Ext.define('Ext.ux.ServerUtilities', 
{
    alias: 'SvrUtils', 
    alternateClassName: 'SvrUtils',
    singleton: true,
    
    requires: [
        'Ext.ux.Log'
    ],

    Log:
    {

        /**
         * @property logTag
         * @private
         * @since 0.16.3
         * 
         * Log tag.
         */
        tag: '[ExtJs]',
        /**
         * @property logTagColor
         * @private
         * @since 0.16.3
         * 
         * Log tag color (HTML code), for example:
         * 
         *     #DD0000
         */
        tagColor: '#00AA31',

        /**
        * Log message.
        *
        * @private
        * @since 0.16.3
        * 
        * @param {String} msg The log message.
        * @param {Number} lvl Log level
        * @param {String} xTag Tag.
        */
        write: function(msg, lvl, xTag)
        {
            Log.write(msg, lvl, false, false, null, this.tag + (xTag ? '[' + xTag + ']' : ''), this.tagColor);
        },

        /**
        * Log value message.
        *
        * @private
        * @since 0.16.3
        * 
        * @param {String} msg The log message.
        * @param {Object|String|Number} value Log value.
        * @param {Number} lvl Log level.
        * @param {String} xTag Tag.
        */
        value: function(msg, value, lvl, xTag)
        {
            Log.value(msg, value, lvl, false, false, this.tag + (xTag ? '[' + xTag + ']' : ''), this.tagColor);
        }
    }

});
